import React from 'react';
import ReactDOM from 'react-dom';
import Modal from 'react-modal';
import styles from './styles.module.css'

const customStyles = {
    content: {
        top: '50%',
        left: '50%',
        right: 'auto',
        bottom: 'auto',
        marginRight: '-50%',
        transform: 'translate(-50%, -50%)',
    }
};
    const Button = () => {

        let subtitle;
        const [modalIsOpen, setIsOpen] = React.useState(false);
        function openModal() {
            setIsOpen(true);
        }
    
        function afterOpenModal() {
            subtitle.style.color = '#a00101';
        }
    
        function closeModal() {
            setIsOpen(false);
        }

        const options = [
            { value: 'detectives', label: 'Detectives' },
            { value: 'comics and manga', label: 'Comics and manga' },
            { value: 'romance', label: 'Romance' },
            { value: 'poetry', label: 'Poetry' },
            { value: 'adventures', label: 'Adventures' },
            { value: 'prose', label: 'Prose' },
            { value: 'thrillers', label: 'Thrillers' },
            { value: 'horror', label: 'Horror' },
        
        ]

        return (

            <div>
                <button className={styles.start} onClick={openModal}>Create fanfic</button>
                <Modal
                    isOpen={modalIsOpen}
                    onAfterOpen={afterOpenModal}
                    onRequestClose={closeModal}
                    style={customStyles}
                    className={styles.modal}
                    contentLabel="Example Modal"
                >

                    <h2 ref={_subtitle => (subtitle = _subtitle)} className={styles.fanfic}>New Fanfic</h2>
                    <div className={styles.wrappForm}>
                    <label className={styles.labels}  >Name of the fanfic*</label>
                        <input type="text" name= "name" className={styles.inputColumn} ></input> 
                    </div>
                    <div className={styles.wrappForm}>
                    <label className={styles.labels}> Genres*</label>
                   
                    <select name="genre" id="genre">
                      <option value='detectives'>Detectives</option>
                      <option value='comics and manga'>Comics and manga</option>
                      <option value='romance'>Romance</option>
                      <option>Poetry</option>
                      <option>Thrillers</option>
                    </select>
                    </div>
                    <div className={styles.wrappForm}>
                    <label className={styles.labels} >Short description*</label>
                        <input type="text" name= "password"  className={styles.inputColumn}></input> 
                    </div>
                    <div>
                    <button> Create</button>
                    <button onClick={closeModal}>close</button>
                   </div>
                </Modal>
            </div>


        )

    }


    export default Button
